import { useEffect, useState } from 'react'
import { StyleSheet, ScrollView, View, ImageBackground, Keyboard, Dimensions } from 'react-native'
import { useRecoilState } from 'recoil'
import moment from 'moment'
import AsyncStorage from '@react-native-async-storage/async-storage'

import store from '../../store'
import Title from '../../components/title'
import InputImage from '../../components/input/image'
import InputText from '../../components/input/text'
import Button from '../../components/button'
import { registrationAPI } from '../../API/REST/auth'

import logo from '../../assets/images/logo.png'

function RegistrationPage() {
  const [, setLoggedInLoading] = useRecoilState(store.loggedInLoading)
  const [, setLoggedIn] = useRecoilState(store.loggedIn)
  const [, setToken] = useRecoilState(store.token)

  const [image, setImage] = useState('data:image/png;base64,')
  const [firstName, setFirstName] = useState('')
  const [lastName, setLastName] = useState('')
  const [dateOfBirth, setDateOfBirth] = useState('')
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [rePassword, setRePassword] = useState('')

  const [keyboardVisible, setKeyboardVisible] = useState(false)
  const [message, setMessage] = useState(null)
  const [loading, setLoading] = useState(false)

  useEffect(() => {
    const keyboardDidShowListener = Keyboard.addListener(
      'keyboardDidShow', () => {
        setKeyboardVisible(true)
      }
    )
    const keyboardDidHideListener = Keyboard.addListener(
      'keyboardDidHide', () => {
        setKeyboardVisible(false)
      }
    )

    return () => {
      keyboardDidHideListener.remove()
      keyboardDidShowListener.remove()
    }
  }, [])

  function uploadImage(image) {
    setImage('data:image/jpeg;base64,' + image)
}

  async function registration() {
    if(firstName === '') {
      setMessage('first name required')
      return 
    }
    if(lastName === '') {
      setMessage('last name required')
      return
    }
    if(dateOfBirth === '') {
      setMessage('date of birth required')
      return
    }
    if(username === '') {
      setMessage('username required')
      return
    }
    if(password === '') {
      setMessage('password required')
      return
    }
    if(rePassword === '') {
      setMessage('re-password required')
      return
    }
    if(password !== rePassword) {
      setMessage('password did not match re-password')
      return
    }
    if(!moment(dateOfBirth, 'DD/MM/YYYY', true).isValid()) {
      setMessage('date format not correct')
      return
    }
    setLoading(true)
    try {
      setLoggedInLoading(true)
      const registrationResponse = await registrationAPI(image, firstName, lastName, moment(dateOfBirth, 'DD/MM/YYYY').format('YYYY-MM-DD'), username, password)
      await AsyncStorage.setItem('token', registrationResponse.headers.authorization)
      setToken(registrationResponse.headers.authorization)
      setLoggedInLoading(false)
      setLoggedIn(true)
      setLoading(false)
    }
    catch(error) {
      setLoggedInLoading(false)
      setLoading(false)
      setMessage(error.response.data)
      return
    }
  }

  function removeMessage() {
    setMessage(null)
  }

  return (
    <ScrollView>
      <View
        style={[styles.wrapper, (keyboardVisible && styles.wrapperWithKeyboard)]}
      >
        <View
          style={styles.window}
        >
          <ImageBackground
            imageStyle={styles.backgroundImage}
            source={logo}
            resizeMode='contain'
          >
            <Title
              text='registration'
              textStyle={styles.titleText}
            />
            <View
              style={styles.inputImageWrapper}
            >
              <InputImage
                image={image}
                changeImage={uploadImage}
                wrapperStyle={styles.inputImageWindow}
                imageStyle={styles.inputImageImage}
                buttonStyle={styles.inputImageButton}
                buttonImageStyle={styles.inputImageButtonImage}
                cameraButtonStyle={styles.inputImageCameraButton}
                galleryButtonStyle={styles.inputImageGalleryButton}
              />
            </View>
            <View
              style={styles.formRow}
            >
              <InputText
                removeMessage={removeMessage}
                password={false}
                showLabel={true}
                label='first name'
                text={firstName}
                changeText={setFirstName}
                placeholder={null}
                wrapperStyle={styles.inputTextWrapper}
                labelTextStyle={styles.inputTextlabelText}
                inputTextStyle={styles.inputTextInputText}
              />
              <InputText
                removeMessage={removeMessage}
                password={false}
                showLabel={true}
                label='last name'
                text={lastName}
                changeText={setLastName}
                placeholder={null}
                wrapperStyle={styles.inputTextWrapper}
                labelTextStyle={styles.inputTextlabelText}
                inputTextStyle={styles.inputTextInputText}
              />
            </View>
            <View
              style={styles.formRow}
            >
              <InputText
                removeMessage={removeMessage}
                password={false}
                showLabel={true}
                label='date of birth'
                text={dateOfBirth}
                changeText={setDateOfBirth}
                placeholder='DD/MM/YYYY'
                wrapperStyle={styles.inputTextWrapper}
                labelTextStyle={styles.inputTextlabelText}
                inputTextStyle={styles.inputTextInputText}
              />
              <InputText
                removeMessage={removeMessage}
                password={false}
                showLabel={true}
                label='username'
                text={username}
                changeText={setUsername}
                placeholder={null}
                wrapperStyle={styles.inputTextWrapper}
                labelTextStyle={styles.inputTextlabelText}
                inputTextStyle={styles.inputTextInputText}
              />
            </View>
            <View
              style={styles.formRow}
            >
              <InputText
                removeMessage={removeMessage}
                password={true}
                showLabel={true}
                label='password'
                text={password}
                changeText={setPassword}
                placeholder={null}
                wrapperStyle={styles.inputTextWrapper}
                labelTextStyle={styles.inputTextlabelText}
                inputTextStyle={styles.inputTextInputText}
              />
              <InputText
                removeMessage={removeMessage}
                password={true}
                showLabel={true}
                label='re-password'
                text={rePassword}
                changeText={setRePassword}
                placeholder={null}
                wrapperStyle={styles.inputTextWrapper}
                labelTextStyle={styles.inputTextlabelText}
                inputTextStyle={styles.inputTextInputText}
              />
            </View>
            <Button
              loading={loading}
              showMessage={true}
              messageText={message}
              work={loading ? () => {} : registration}
              buttonText='submit'
              wrapperStyle={styles.buttonAndMessageWrapper}
              buttonWrapperStyle={styles.buttonWrapper}
              buttonTextStyle={styles.buttonText}
              messageWrapperStyle={[styles.messageWrapper, styles.messageFailWrapper, ((message === null) && styles.messageHiddenWrapper)]}
              messageTextStyle={styles.messageText}
            />
          </ImageBackground>
        </View>
      </View>
    </ScrollView>
  )
}

const styles = StyleSheet.create({
  wrapper: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',

    minHeight: Dimensions.get('window').height - 100,

    backgroundColor: '#000000',

    paddingBottom: 80,
    paddingLeft: 10,
    paddingRight: 10
  },
  wrapperWithKeyboard: {
    justifyContent: 'flex-start',

    paddingTop: 20,
    paddingBottom: 0
  },

  window: {
    width: '100%',

    padding: 20,
    paddingTop: 10,

    backgroundColor: '#e6e6e6',

    borderRadius: 10
  },

  backgroundImage: {
    opacity: 0.1
  },

  titleText: {
    fontFamily: 'Ubuntu_700Bold',
    fontSize: 35,
    textTransform: 'uppercase',
    textAlign: 'center',

    padding: 20
  },

  inputImageWrapper: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'
  },
  inputImageWindow: {
    backgroundColor: '#ffffff',

    width: 100,
    height: 100,

    padding: 5,

    borderRadius: 50,

    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center'

  },
  inputImageImage: {
    width: 85,
    height: 85,

    borderRadius: 43
  },
  inputImageButton: {
    position: 'absolute',

    backgroundColor: '#e04f5f',

    borderRadius: 15,

    padding: 5
  },
  inputImageButtonImage: {
    width: 20,
    height: 20
  },
  inputImageCameraButton: {
    top: 70,
    left: 80,
  },
  inputImageGalleryButton: {
    top: 0,
    left: 80,
  },

  formRow: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between'
  },

  inputTextWrapper: {
    marginTop: 7,
    marginBottom: 7,

    width: '45%'
  },
  inputTextlabelText: {
    fontFamily: 'Ubuntu_400Regular',
    fontSize: 15,
    textTransform: 'uppercase',

    marginBottom: 5,
    marginLeft: 10
  },
  inputTextInputText: {
    width: '100%',

    fontFamily: 'Ubuntu_400Regular',
    fontSize: 17,

    padding: 10,

    backgroundColor: '#ffffff',

    borderRadius: 10
  },

  buttonAndMessageWrapper: {
    marginTop: 20,
  },
  buttonWrapper: {
    padding: 10,

    borderRadius: 10,

    backgroundColor: '#e04f5f',

    minHeight: 42
  },
  buttonText: {
    fontFamily: 'Ubuntu_400Regular',
    fontSize: 18,
    textTransform: 'uppercase',
    textAlign: 'center'
  },
  messageWrapper: {
    marginTop: 15,

    padding: 10,

    borderRadius: 10
  },
  messageFailWrapper: {
    backgroundColor: '#e04f5f'
  },
  messageHiddenWrapper: {
    opacity: 0
  },
  messageText: {
    fontFamily: 'Ubuntu_400Regular',
    fontSize: 15,
    textTransform: 'uppercase',
    textAlign: 'center',
    color: '#ffffff'
  }
})

export default RegistrationPage